import { useState } from "react";
import { Link, useLocation } from "react-router-dom";

const AddCart = () => {
    const location = useLocation();
    const getBookData = location.state && location.state.getBookData; // getBookData is from home.js using state
    const [total, setTotal] = useState(0);
    // get total amount
    //   const totalAmount = prices.reduce((total, price) => total + price, 0);

    // setTotal(getBookData.price.reduce((total, price) => total + price, 0))

    // const getBooks = location.state && location.state.getBooks;
    // console.log(getBookData, "[[[[[[[[[[")
    return (
        <div className="table-cart m-4">
            <div className="float-end">
                <Link className="btn btn-primary mb-4" to={'/'}>Add Item</Link>
            </div>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <td><b>Action</b></td>
                        <td><b>S.No</b></td>
                        <td><b>Book Name</b></td>
                        <td><b>Author</b></td>
                        <td><b>Price</b></td>
                    </tr>
                </thead>
                <tbody>
                    {
                        getBookData && getBookData.map((item) => (
                            <>
                                <tr key={item.id}>
                                    <td>
                                        <button className="btn btn-danger">Remove</button>
                                    </td>
                                    <td>{item.id}</td>
                                    <td>{item.title}</td>
                                    <td>{item.author}</td>
                                    <td>{item.price}</td>

                                </tr>
                                <tr>
                                    Total Amount : {total}
                                </tr>
                            </>
                        ))
                    }
                    <tr>

                    </tr>
                </tbody>
            </table>
        </div>
    );
};

export default AddCart;
