import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Home = (props) => {
    console.log(props, "hoe")
    const navigate = useNavigate();
    // const location = useLocation();
    useEffect(() => {
        // this is for redirect the login page if the user is not valid 
        let username = sessionStorage.getItem('username')
        if (username === null || username === '') {
            navigate('/login')
        };

        getUserList();

    }, []);
    const getUserList = () => {
        fetch("http://localhost:3000/user").then((res) => { // here we need to call post method by sending username and also call get method 
            return res.json();
        }).then((resp) => {
            console.log(resp)
            const loginResponse = resp;
