import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Home = (props) => {
    const [responseVal, loginResponse] = useState("")
    const navigate = useNavigate();
    // const location = useLocation();
    useEffect(() => {
        // this is for redirect the login page if the user is not valid 
        let username = sessionStorage.getItem('username')
        if (username === null || username === '') {
            navigate('/login')
        };

        getUserList();

    }, []);
    const getUserList = () => {
        fetch("http://localhost:3000/user").then((res) => { // here we need to call post method by sending username and also call get method 
            return res.json();
        }).then((resp) => {
            console.log(resp)
            loginResponse(resp)
            console.log("loginResponse", responseVal)
            // if (Object.keys(resp).length === 0) {
            //     toast.warn("Please enter valid user")
            // }
            // else {
            //     if (resp.password === password) { // the second password from usestate
            //         toast.success("Success")
            //         sessionStorage.setItem("username", username)
            //         navigate('/', { state: { loginResponse } })
            //     } else {
            //         toast.error("Please enter valid credentials")
            //     }
            // }
        }).catch((err) => {
            toast.error(err.message)
        })
    }
    // const loginResponse = location.state && location.state.loginResponse;
    // console.log(loginResponse, "login_resssssss")
    return (
        <div>
            <div className="header">
                <Link to={'/'}>Home</Link>
                <Link to={'/login'}>Logout</Link>
            </div>
            <h2>User Details</h2>
            <div className="Table-Data">
                <div className="float-end mb-3 mr-5">
                    <Link className="btn btn-primary">Add User</Link>
                </div>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <td>Name</td>
                            <td>Email</td>
                            <td>Address</td>
                            <td>Country</td>
                            <td>Gender</td>
                            <td>Phone</td>
                            <td>Action</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            responseVal && responseVal.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.address}</td>
                                    <td>{item.country}</td>
                                    <td>{item.gender}</td>
                                    <td>{item.phone}</td>
                                    <td className="d-flex align-items-center">
                                        <button className="btn btn-info" >Edit</button>
                                        {/* <button className="btn btn-primary">View</button> */}
                                        <button className="btn btn-danger">Delete</button>
                                    </td>
                                </tr>
                            ))
                        }

                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Home;
